/// <reference types="nativewind/types" />

// src/data/file_type_declarations.d.ts

// Image formats
declare module '*.png' {
    const value: any;
    export default value;
  }
  
  declare module '*.jpg' {
    const value: any;
    export default value;
  }
  
  declare module '*.jpeg' {
    const value: any;
    export default value;
  }
  
  declare module '*.gif' {
    const value: any;
    export default value;
  }
  
  declare module '*.svg' {
    const content: any;
    export default content;
  }

  declare module '*.webp' {
    const content: any;
    export default content;
  }

  
  
  // Video formats
  declare module '*.mp4' {
    const src: string;
    export default src;
  }
  
  declare module '*.webm' {
    const src: string;
    export default src;
  }
  
  declare module '*.ogg' {
    const src: string;
    export default src;
  }
  
  // Audio formats
  declare module '*.mp3' {
    const src: string;
    export default src;
  }
  
  declare module '*.wav' {
    const src: string;
    export default src;
  }
  
  declare module '*.m4a' {
    const src: string;
    export default src;
  }
  
  // Other common media formats
  declare module '*.pdf' {
    const src: string;
    export default src;
  }
  
  declare module '*.txt' {
    const src: string;
    export default src;
  }
  
  declare module '*.docx' {
    const src: string;
    export default src;
  }
  
  declare module '*.xlsx' {
    const src: string;
    export default src;
  }
  
